    SCENARIO.scenario_body.bsp_switch_trigger_volumes_tag_block = TAG.TagBlock(scenario_body_struct[117], 0, scenario_body_struct[118], scenario_body_struct[119])
    SCENARIO.scenario_body.decals_tag_block = TAG.TagBlock(scenario_body_struct[120], 0, scenario_body_struct[121], scenario_body_struct[122])
