    SCENARIO.scenario_body.ai_conversations_tag_block = TAG.TagBlock(scenario_body_struct[159], 0, scenario_body_struct[160], scenario_body_struct[161])
    SCENARIO.scenario_body.script_syntax_data_tag_data = TAG.RawData(scenario_body_struct[162], scenario_body_struct[163], scenario_body_struct[164], scenario_body_struct[165], scenario_body_struct[166])
