    SCENARIO.scenario_body.cutscene_flags_tag_block = TAG.TagBlock(scenario_body_struct[187], 0, scenario_body_struct[188], scenario_body_struct[189])
    SCENARIO.scenario_body.cutscene_camera_points_tag_block = TAG.TagBlock(scenario_body_struct[190], 0, scenario_body_struct[191], scenario_body_struct[192])
