    SCENARIO.scenario_body.chapter_title_text_tag_ref = TAG.TagRef(scenario_body_struct[200].decode().rstrip('\x00'), "", scenario_body_struct[202] + 1, scenario_body_struct[201], scenario_body_struct[203])
    SCENARIO.scenario_body.hud_messages_tag_ref = TAG.TagRef(scenario_body_struct[204].decode().rstrip('\x00'), "", scenario_body_struct[206] + 1, scenario_body_struct[205], scenario_body_struct[207])
    SCENARIO.scenario_body.structure_bsps_tag_block = TAG.TagBlock(scenario_body_struct[208], 0, scenario_body_struct[209], scenario_body_struct[210])
    SCENARIO.scenario_body.scenario_resources_tag_block = TAG.TagBlock(scenario_body_struct[211], 0, scenario_body_struct[212], scenario_body_struct[213])
    SCENARIO.scenario_body.old_structure_physics_tag_block = TAG.TagBlock(scenario_body_struct[214], 0, scenario_body_struct[215], scenario_body_struct[216])
