    SCENARIO.scenario_body.unused_1_tag_block = TAG.TagBlock(scenario_body_struct[244], 0, scenario_body_struct[245], scenario_body_struct[246])
    SCENARIO.scenario_body.unused_2_tag_block = TAG.TagBlock(scenario_body_struct[247], 0, scenario_body_struct[248], scenario_body_struct[249])
